#include <iostream>
#include <algorithm>
#include <map>
#include <string>
#include <vector>
#include <list>
#include <stdlib.h>
#include <math.h>
using namespace std;

map<char, char> cc = { { '(', ')' } };

bool isValid(string s) {
	vector<char> a;
	int  lenth = s.length();
	int i = 0;
	if (lenth == 2)
	{
		if (s.at(1) == cc[s.at(0)]) return true;
		else return false;
	}
	else if (lenth % 2 != 0) return false;
	else if (lenth == 0) return true;
	else
	{
		for (i = 0; i < s.length(); i++)
		{
			if ((s.at(i) == '(' ))
			{
				a.push_back(s.at(i));
			}
			else if ((s.at(i) == ')' ) && a.size() == 0)
			{
				return false;
			}
			else if ((s.at(i) == ')') && a.size() != 0)
			{
				if (s.at(i) != cc[a.back()]) return false;
				else
				{
					a.pop_back();
					continue;
				}
			}
		}
	}
	if (a.size() != 0)
	{
		a.clear();
		return false;
	}
	return true;
}


int longestValidParentheses(string s) {
	int i = 0;
	int j = 0;
	int cnt_l = 0;
	int cnt_r = 0;
	int length = s.length();
	int num_l = 0; //用于统计 右括号应该移动几个
	int begin_cnt_l = 0;//用于内层计数
	int begin_cnt_r = 0;
	int max_res = 0;
	vector<int> res;
	if (s.empty() || length == 1) return 0;
	else
	{
		int l = 0;
		int r = length - 1;
		if (s.at(l) == ')')
		{
			l++;
			while (l < r && s.at(l) == ')') ++l;
		}
		if (l >= r) return 0;
		if (s.at(r) == '(')
		{
			r--;
			while (l < r && s.at(r) == '(') --r;
		}
		if (l >= r) return 0;
		//此时 l和 r 是这个字符串中第一个出现( 和 最后一次出现）的位置
		for (i = l; i <= r; i++)
		{
			if (s.at(i) == '(') cnt_l++;
			if (s.at(i) == ')') cnt_r++;
		}
		for (i = l; i < r;)
		{
			begin_cnt_l = cnt_l;
			begin_cnt_r = cnt_r;
			for (j = r; j > i;)
			{
				if (begin_cnt_l == begin_cnt_r && isValid(s.substr(i, j - i + 1)))
				{
					max_res = max(max_res, j-i+1);
					i = j + 1;
					break;
				}
				if (s.at(j) == '(') begin_cnt_l--;
				if (s.at(j) == ')') begin_cnt_r--;
				j--;
			}
			if (i > r) break;
			if (s.at(i) == '(') cnt_l--;
			if (s.at(i) == ')') cnt_r--;
			i++;
		}


		//此时l和r是区域里最包含的最长等长度配准 起始位置
		return max_res;
	}
}



